std::vector<std::pair<int,int>> vector_map;
std::mt19937 g;
bool use_vector_map = false;
//static int char_range=10;  //for numeric text
//static int char_range=36;  //for alphanumeric text
//static int shuf_range=255;
void init_vector_map(string keyPhrase, int char_range, int shuf_range)
{
    if (use_vector_map)
    {
        std::cout << "Vector Map is already initialised" << endl; 
        vector_map.clear();
        std::cout << "Vector Map cleared" << endl; 
    }
    int uni_dist_max = (shuf_range/char_range) -2;
    if (uni_dist_max < 1)
    {
        uni_dist_max = 1;
    }
    std::uniform_int_distribution<int> uni(0,uni_dist_max); // guaranteed unbiased
    //std::random_device rd;
    //use keyPhrase to create seed sequence
    std::seed_seq sseq(keyPhrase.begin(),keyPhrase.end());
    g.seed(sseq);
                int start=-1;
                int end=-1;
                for (int a = 0; a < char_range; a+=1)
                {
                    start = end+1;
                    if (a==char_range-1)
                    {
                       end = shuf_range;
                    }
                    else
                    {
                        end = start + uni(g);
                        //check not exceeded max
                        if (end >= shuf_range)
                        {
                            cout << "Setting end to " << start << " in map init " << endl;
                            end = start;
                        }
                        else if (shuf_range - end < char_range - a)
                        {
                            //we have run out of space. set end = start
                            end = start;
                        }
                    }
                    std::pair<int, int> p = {start,end};
                    vector_map.push_back(p);
                }
                //for (int a = 0; a < char_range; a+=1)
                //{
    //    std::cout << a << "th item start is " << vector_map[a].first << " and end is " << vector_map[a].second << "\n";                         
                //}
    //shuffle the vector map           
    std::shuffle(vector_map.begin(), vector_map.end(), g);
                //for (int a = 0; a < char_range; a+=1)
                //{
    //    std::cout << a << "th item start is " << vector_map[a].first << " and end is " << vector_map[a].second << "\n";                         
                //}
    use_vector_map = true;
}
 
unsigned int arr_position(char input_char)
{
                //cout << "In arr_position for char " << input_char <<endl;
                int offset = 48;
                // cast char to int
                unsigned int iValue = unsigned(input_char);
                //cout << "iValue is " << iValue <<endl;
                if (iValue > 57)
                {
                                offset = 87;
                }
                iValue = iValue - offset;
                //iValue now in range 0-35
                return iValue;
}
 
unsigned int map_lower(char input_char)
{
    //cout << "In map_lower for char " << input_char <<endl;
                unsigned int i = 0;
    unsigned int iValue = arr_position(input_char);
                //cout << "iValue is " << iValue <<endl;
    if (use_vector_map)
                {
                    i = vector_map[iValue].first;
                }
                else
                {
                    while (char_map[iValue][i] == false) i++;
                }
                return i;
}
unsigned int map_upper(char input_char)
{
                unsigned int iValue = arr_position(input_char);
                unsigned int i = 255;
    if (use_vector_map)
                {
                    i = vector_map[iValue].second;
                }
                else
                {
                    while (char_map[iValue][i] == false) i--;
                }
                return i;
}
unsigned int map_shuffle(char input_char)
{
                unsigned int iValue = arr_position(input_char);
                unsigned int low_value = map_lower(input_char);
                unsigned int high_value = map_upper(input_char);
                //cout << "In map shuffle with " << low_value << " low val and " << high_value << " hig val" << endl;
                if (low_value == high_value)
                {
                  return low_value;
                }
                else
                {  
                //generate random number between low_value and high_value
                std::random_device rd;     // only used once to initialise (seed) engine
                std::mt19937 rng(rd());    // random-number engine used (Mersenne-Twister in this case)
                std::uniform_int_distribution<int> uni(low_value,high_value); // guaranteed unbiased
                unsigned int random_integer = uni(rng);
                //cout << "low_value is " << low_value << " high value is " << high_value << " random offset is " << random_integer << endl;
               
                return random_integer;
                }
}
unsigned int unmap_shuffle(unsigned int input_int)
{
                unsigned int i = 0;
    if (use_vector_map)
                {
                    //cout << "unmap int value is " << input_int << endl;
        while (!(vector_map[i].first <= input_int && vector_map[i].second >= input_int)) i++;
                                //cout << "calculated position is " << i << endl;
                }
                else
                {
        while (char_map[i][input_int] == false) i++;
                }
                if (i > 9)
                {
                                i += 87;
                }
                else
                {
                                i += 48;
                }
                return i;
}
bool is_number(const std::string& s)
{
    std::string::const_iterator it = s.begin();
    while (it != s.end() && std::isdigit(*it)) ++it;
    return !s.empty() && it == s.end();
}
 